#Solution needs to improve O(n^2) time complexity
class Solution(object):
    def palindromePairs(self, words):
        """
        :type words: List[str]
        :rtype: List[List[int]]
        """
        solution = []
        for i in range(len(words) - 1):
            for j in range(i + 1, len(words)):
                #print words[i], words[j], words[i]+ words[j]
                if words[i] + words[j] == (words[i] + words[j])[::-1]:
                    solution.append([i, j])
                if words[j] + words[i] == (words[j] + words[i])[::-1]:
                    solution.append([j, i])
        
        return solution

